import React, { useEffect } from "react";
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBBtn,
} from "mdb-react-ui-kit";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import { getCartTotal } from "../features/cartSlice";

export default function App() {
  const { cart, totalQuantity } = useSelector((state) => state.allCart);

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getCartTotal());
  }, [cart]);

  return (
    <MDBNavbar light bgColor="light">
      <MDBContainer fluid>
        <MDBNavbarBrand><img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQgzqEsom1-lZHqj_2x3wagYGkGz0Y3f5_0okUiUOGKg-7RJCpub3gyqBRnDBOar2bPuoI&usqp=CAU" alt="..."/></MDBNavbarBrand>
        <span>
          <Link to="/">All Product </Link>
        </span>
        <MDBBtn color="light">
          <Link to="/cart"><img src="https://img.icons8.com/?size=1x&id=vQcDKfWBNewU&format=png" alt="..."/>
          <span className="cart-count">  {totalQuantity}</span></Link>
        </MDBBtn>
      </MDBContainer>
    </MDBNavbar>
  );
}
